import axios from 'axios';
import AlertContext from './alertContext';
import AlertReducer from './alertReducer';
import {
SET_ALERT,
REMOVE_ALERT
} from '../types';

const AlertState = props => {
    const initialState = null;

    const [state, dispatch] = useReducer(AlertReducer, initialState);

    //Set Alert

